import React, { useState } from "react";
import Header from "../../components/header";
import "../../styles/styles.css";
import downloadImage from "../../assets/download.jpeg";
import downloadImage1 from "../../assets/download1.jpeg";

const Product = () => {
  const x = [downloadImage, downloadImage1, downloadImage, downloadImage];
  const [selectImage, setSelectImage] = useState(x[0]);
  return (
    <div>
      <Header />
      <div className="d-flex gap-2">
        <p>hello 1</p>
        <p>{">>"}</p>
        <p>hello 2</p>
        <p>{">>"}</p>
        <p>hello 3</p>
      </div>
      <div className="text-center h1">Product</div>

      <div className="d-flex px-5">
        <div className="w-75">
          <div className="d-flex justify-content-center border ">
            <div
              onClick={() => {
                const index = x.indexOf(selectImage);
                if (index === 0) {
                  setSelectImage(x[0]);
                }
                setSelectImage(x[index - 1]);
              }}
            >
              previes
            </div>
            <img src={selectImage} className="w-75 h-100" alt="" />
            <div
              onClick={() => {
                const index = x.indexOf(selectImage);
                if (index === x.length - 1) {
                  setSelectImage(x[0]);
                }
                setSelectImage(x[index + 1]);
              }}
            >
              next
            </div>
          </div>
          <div>
            {x.map((item, index) => (
              <img src={item} key={index} className="w-25 h-25" alt="" />
            ))}
          </div>
        </div>
        <div className="w-25 border">
<div></div>

        </div>
      </div>
    </div>
  );
};

export default Product;
